mapper.select:
SELECT * FROM post_comment
WHERE post_id = #{postId}
ORDER BY created_at ASC

mapper.update:
UPDATE post_comment
SET content = #{content}
WHERE id = #{id}
  AND family_member_id = #{familyMemberId}

mapper.delete:
DELETE FROM post_comment
WHERE id = #{id}
  AND family_member_id = #{familyMemberId}
